export default function ElegantBackground() {
  const [offset, setOffset] = useState(0)
  const [techs, setTechs] = useState<Technology[]>([])

  const createTechnology = useCallback(() => {
    const tech = technologies[Math.floor(Math.random() * technologies.length)]
    return {
      id: Math.random(),
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 7 + 5, // 10-30px
      opacity: Math.random() * 0.05 + 0.04, // 0.03-0.1
      speed: Math.random() * 0.25 + 0.1, // 0.1-0.6
      icon: tech.path,
    }
  }, [])

  useEffect(() => {
    const animateBackground = () => {
      setOffset((prevOffset) => (prevOffset + 0.2) % 100)
    }

    const intervalId = setInterval(animateBackground, 50)

    return () => clearInterval(intervalId)
  }, [])

  useEffect(() => {
    // Initialize technologies
    setTechs(Array.from({ length: 20 }, createTechnology))

    const animateTechs = () => {
      setTechs((prevTechs) =>
        prevTechs.map((tech) => ({
          ...tech,
          size: tech.size + tech.speed,
          opacity: tech.opacity - 0.0005,
        }))
      )
    }

    const addNewTechs = () => {
      setTechs((prevTechs) => [
        ...prevTechs.filter((tech) => tech.opacity > 0),
        createTechnology(),
      ])
    }

    const techIntervalId = setInterval(animateTechs, 50)
    const newTechIntervalId = setInterval(addNewTechs, 500)

    return () => {
      clearInterval(techIntervalId)
      clearInterval(newTechIntervalId)
    }
  }, [createTechnology])

  return (
    <div className="fixed -z-50 inset-0 overflow-hidden" style={{ backgroundColor: '#161616' }}>
      <div className="absolute inset-0 overflow-hidden">
        {/* Blurred shapes */}
        <div
          className="absolute inset-0 opacity-20 blur-3xl"
          style={{
            backgroundImage: `
              radial-gradient(circle at 20% 30%, #5a5959 0%, transparent 40%),
              radial-gradient(circle at 80% 70%, #4a4949 0%, transparent 40%)
            `,
            transform: `translateY(${offset}px)`,
          }}
        />

        {/* Subtle lines */}
        <div className="absolute inset-0 opacity-10">
          <div className="h-full w-[1px] bg-gradient-to-b from-transparent via-gray-300 to-transparent absolute left-1/4" />
          <div className="h-full w-[1px] bg-gradient-to-b from-transparent via-gray-300 to-transparent absolute left-2/4" />
          <div className="h-full w-[1px] bg-gradient-to-b from-transparent via-gray-300 to-transparent absolute left-3/4" />
        </div>

        {/* Animated technology icons */}
        {techs.map((tech) => (
          <svg
            key={tech.id}
            className="absolute"
            style={{
              left: tech.x,
              top: tech.y,
              width: tech.size,
              height: tech.size,
              opacity: tech.opacity,
            }}
            viewBox="0 0 24 24"
            fill="currentColor"
          >
            <path d={tech.icon} />
          </svg>
        ))}

        {/* Gradient overlay */}
        <div className="absolute inset-0 bg-gradient-to-br from-transparent to-[#2a2929] opacity-50" />
      </div>
    </div>
  )
}